  submitFeedback(){
    let form = {
      feedback:this.message,
      employeeId:localStorage.getItem('userId'),
      organisationId:this.authService.organisationId,
      date:new Date()
    }

    this.authService.createFeedback(form).then((res:any)=>{
      if(res.feedbackId){
        this.getFeedback()
      }
    })
  }

  getFeedback(){
    this.authService.getFeedback({organisationId:this.authService.organisationId}).then((res:any)=>{
      console.log(res)

      res?.forEach(element => {
        this.authService.getUserDetails(element.employeeId, 'employee').then((data: any) => {
          console.log('qwerty',data)

          element.name = data.firstName + ' ' + data.lastName
          element.image = data.image
        })
      });


      this.posts = res.length > 0 ? res : this.posts
    })
  }
